export default function RenderizarFlashcard({ data, index }) {
    const [aberta, setAberta] = React.useState(false);

    function teste() {
        if (data.estado === 'fechada') {
            return (
        <div className='pergunta'>
            <h3>Flashcard {index}</h3>
            <ion-icon name="play-outline"></ion-icon>
        </div>)
    } else {
        return (
        <>
        <div className={`flashcardFrente ${aberta ? 'oculta' : ''}`} >
            {data.pergunta}
            <img src="./assets/img/setinha.png" alt="setinha virar" onClick={() => setAberta(true)}/>
        </div>
        <div className={`flashcardVerso ${aberta ? '' : 'oculta'}`}>
            {data.resposta}
