import React from 'react';

export default function RenderizarFlashcard({ data, index }) {
    const [status, setStatus] = React.useState('fechada');
    const [frente, setFrente] = React.useState(false);

    function teste() {
        if (status === 'fechada') {
            return (
                <li className='pergunta' key={index} onClick={() => setStatus('aberta')}>
                    <h3>Flashcard {index}</h3>
                    <ion-icon name="play-outline"></ion-icon>
                </li>)
        } else {
            return (
            <li className='flashcard' key={index}>
                <div className={`frente ${frente ? 'oculta' : ''}`} >
                    {data.pergunta}
                    <img src="./assets/img/setinha.png" alt="setinha virar" onClick={() => setFrente(true)}/>
                </div>
